#!/usr/bin/env node

const fs = require("fs-extra");
const path = require("path");

const folder = process.cwd();

// console.log(`folder: ${folder}`);

fs.copy(path.resolve(__dirname, "./resources/"), folder)
  .then(function() {

    // Rename the npm ignore file.
    return fs.move(
      path.resolve(folder, "./_npmignore"),
      path.resolve(folder, "./.npmignore"));
  })
  .catch(function(err) {
    console.error("Failed to install sandbox: " + err);
  });
